mapper.select:
select * FROM challComment
where CHALLENGEID =#{challengId}
ORDER BY commentDate

mapper.insert:
INSERT INTO CHALLCOMMENT ( COMMENTID, CHALLENGEID, USERID, CHALLCOMMENT, COMMENTDATE, PARENTCOMMENTID ) values (CHALLCOMMENT_SEQ.nextval, #{challengeId}, #{userId}, #{challComment}, #{commentDate},
#{parentCommentId}
NULL
)

mapper.delete:
delete challComment
where COMMENTID =#{commentId}
AND USERID= #{userId}

mapper.update:
UPDATE challComment
   SET CHALLCOMMENT = #{challComment},COMMENTDATE=SYSDATE          WHERE commentId = #{commentId}
     AND userId = #{userId}
